            amount: enteredAmount,
            date: new Date(enteredDate)
        };
        props.onSaveExpenseData(expenseData);
        setEnteredTitle('');
        setenteredAmount('');
        setenteredDate('');
    };
    return  <div className="new-expense__actions">
    <button type="submit">Add New Expense</button>
</div>

};

export default ExpenseForm;
